
public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
            int a=20;
    		int b=30;
    		  System.out.println("Before Swap: "+a+" "+b);
          a=a+b;  
          b=a-b;  
          a=a-b;  
          System.out.println("After Swap: "+a+" "+b);
	}

}
